var darkSwitch = document.getElementsByClassName("darkSwitch")[0];
var darkSwitcht = document.getElementsByClassName("darkSwitch")[1];

window.addEventListener("load", (function () {
    if (darkSwitch) {
        initTheme();
        darkSwitch.addEventListener("change", (function () {
            resetTheme()
        }))
        
    }else{
        WhiteTheme();
        darkSwitch.addEventListener("change", (function () {
            resetTheme()
        }))
    }
    if (darkSwitcht) {
        initThemet();
        darkSwitcht.addEventListener("change", (function () {
            resetThemet()
        }))
        
    }else{
        WhiteThemet();
        darkSwitcht.addEventListener("change", (function () {
            resetThemet()
        }))
    }
}));

function initTheme() {
    var darkThemeSelected = localStorage.getItem("darkSwitch") !== null && localStorage.getItem("darkSwitch") === "dark";
    darkSwitch.checked = darkThemeSelected;
    darkThemeSelected ? document.body.setAttribute("data-theme", "dark") : document.body.removeAttribute("data-theme")
}

function WhiteTheme() {
    var darkThemeSelected = localStorage.getItem("darkSwitch") !== null && localStorage.getItem("darkSwitch") === "whitet";
    darkSwitch.checked = darkThemeSelected;
    darkThemeSelected ? document.body.setAttribute("data-theme", "whitet") : document.body.removeAttribute("data-theme")
}

function resetTheme() {
    if (darkSwitch.checked) {
        document.body.setAttribute("data-theme", "dark");
        localStorage.setItem("darkSwitch", "dark")
    } else {
        document.body.setAttribute("data-theme", "whitet");
        localStorage.setItem("darkSwitch", "whitet")
    }
}

function initThemet() {
    var darkThemeSelected = localStorage.getItem("darkSwitch") !== null && localStorage.getItem("darkSwitch") === "dark";
    darkSwitcht.checked = darkThemeSelected;
    darkThemeSelected ? document.body.setAttribute("data-theme", "dark") : document.body.removeAttribute("data-theme")
}

function WhiteThemet() {
    var darkThemeSelected = localStorage.getItem("darkSwitch") !== null && localStorage.getItem("darkSwitch") === "whitet";
    darkSwitcht.checked = darkThemeSelected;
    darkThemeSelected ? document.body.setAttribute("data-theme", "whitet") : document.body.removeAttribute("data-theme")
}

function resetThemet() {
    if (darkSwitcht.checked) {
        document.body.setAttribute("data-theme", "dark");
        localStorage.setItem("darkSwitch", "dark")
    } else {
        document.body.setAttribute("data-theme", "whitet");
        localStorage.setItem("darkSwitch", "whitet")
    }
}
